import{_ as t,v as e,b as d,R as l}from"./chunks/framework.8277b2e6.js";const h=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep","prev":false,"next":false},"headers":[],"relativePath":"docs/editorconfig.md","filePath":"docs/editorconfig.md","lastUpdated":1695401145000}'),a={name:"docs/editorconfig.md"},o=l(`<h1>.editorconfig</h1><p><a href="https://editorconfig.org/" target="_blank" rel="noreferrer">官网</a> | <a href="https://spec.editorconfig.org/" target="_blank" rel="noreferrer">规范文档</a></p><h2 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>EditorConfig 有助于跨不同编辑器和 IDE 处理同一项目的多个开发人员保持一致的编码风格。</p><p>它可以定义和配置项目中各个文件的缩进、空白符、换行符等格式，并将这些格式规则写入到 <code>.editorconfig</code> 文件中，以便其他开发者可以轻松地设置他们的编辑器来遵循相同的规则。</p><p>支持大部分主流的编辑器和 IDE，包括但不限于：VS Code、Sublime Text、IntelliJ IDEA、Eclipse、Notepad++ 等</p><p>使用 INI 格式，但是允许使用<code>[</code>和<code>]</code>、glob 文件路径（区分大小写），类似<code>.gitignore</code>的格式</p><h2 id="文件格式规范" tabindex="-1">文件格式规范 <a class="header-anchor" href="#文件格式规范" aria-label="Permalink to &quot;文件格式规范&quot;">​</a></h2><h3 id="通配符" tabindex="-1">通配符 <a class="header-anchor" href="#通配符" aria-label="Permalink to &quot;通配符&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">特殊字符</th><th style="text-align:left;">匹配</th></tr></thead><tbody><tr><td style="text-align:left;"><code>*</code></td><td style="text-align:left;">任何字符串，路径分隔符 <code>/</code> 除外</td></tr><tr><td style="text-align:left;"><code>**</code></td><td style="text-align:left;">任意字符串</td></tr><tr><td style="text-align:left;"><code>?</code></td><td style="text-align:left;">任何单个字符，路径分隔符 <code>/</code> 除外</td></tr><tr><td style="text-align:left;"><code>[seq]</code></td><td style="text-align:left;">seq 中的任何单个字符</td></tr><tr><td style="text-align:left;"><code>[!seq]</code></td><td style="text-align:left;">不在 seq 中的任何单个字符</td></tr><tr><td style="text-align:left;"><code>{s1,s2,s3}</code></td><td style="text-align:left;">任何给定的字符串（用逗号分隔，可以嵌套）</td></tr><tr><td style="text-align:left;"><code>{num1..num2}</code></td><td style="text-align:left;"><code>num1</code>和之间的任何整数<code>num2</code>，其中<code>num1</code>和<code>num2</code> 可以是正数或负数</td></tr></tbody></table><p>反斜杠字符 ( <code>\\\\</code>) 可用于转义字符</p><br><h3 id="配置项" tabindex="-1">配置项 <a class="header-anchor" href="#配置项" aria-label="Permalink to &quot;配置项&quot;">​</a></h3><table><thead><tr><th style="text-align:left;">钥匙</th><th style="text-align:left;">支持的值</th></tr></thead><tbody><tr><td style="text-align:left;"><code>indent_style</code></td><td style="text-align:left;">缩进格式，可选：<code>tab</code>和<code>space</code>，不区分大小写。</td></tr><tr><td style="text-align:left;"><code>indent_size</code></td><td style="text-align:left;">缩进宽度，一个整数</td></tr><tr><td style="text-align:left;"><code>tab_width</code></td><td style="text-align:left;">制表符宽度，一个整数，定义制表符的宽度，默认为<code>indent_size</code>，通常不需要指定</td></tr><tr><td style="text-align:left;"><code>end_of_line</code></td><td style="text-align:left;">换行符，可选为<code>lf</code>、<code>cr</code>、 或<code>crlf</code>，不区分大小写</td></tr><tr><td style="text-align:left;"><code>charset</code></td><td style="text-align:left;">编码格式：可选 <code>latin1</code>、<code>utf-8</code>、<code>utf-8-bom</code>、<code>utf-16be</code>或<code>utf-16le</code>，不鼓励<code>utf-8-bom</code></td></tr><tr><td style="text-align:left;"><code>trim_trailing_whitespace</code></td><td style="text-align:left;">设置为<code>true</code>可删除文件中换行符之前的所有空白字符并<code>false</code>确保不会删除。</td></tr><tr><td style="text-align:left;"><code>insert_final_newline</code></td><td style="text-align:left;">true/false，文件是否以换行符结尾</td></tr><tr><td style="text-align:left;"><code>root</code></td><td style="text-align:left;">true/false，是否为根配置文件</td></tr></tbody></table><p>示例</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">root = true</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">[*]</span></span>
<span class="line"><span style="color:#babed8;">charset = utf-8</span></span>
<span class="line"><span style="color:#babed8;">end_of_line = lf</span></span>
<span class="line"><span style="color:#babed8;">indent_style = space</span></span>
<span class="line"><span style="color:#babed8;">indent_size = 2</span></span>
<span class="line"><span style="color:#babed8;">trim_trailing_whitespace = true</span></span>
<span class="line"><span style="color:#babed8;">insert_final_newline = true</span></span>
<span class="line"><span style="color:#babed8;">quote_type = single</span></span></code></pre></div><p>其他的看文档</p>`,17),s=[o];function n(c,i,r,p,f,g){return e(),d("div",null,s)}const b=t(a,[["render",n]]);export{h as __pageData,b as default};
