<span class="line"><span style="color:#BABED8;">      files</span><span style="color:#89DDFF;">:</span><span style="color:#BABED8;"> </span><span style="color:#89DDFF;">[</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">**/*.{html,vue}</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">],</span></span>
